import React from "react";
import PropTypes from 'prop-types';
import { useState } from "react";
import axios from "axios";

const NewLocationForm = (props) => {

    const [formFields, setFormFields] = useState({
            name:'',
            })

    const getCoordinates = (name) => {
        axios.get('https://us1.locationiq.com/v1/search.php', {
            params: {
            key: 'pk.a4ee20e6824496da2fb04ab94b9da2dc',  // discussed below
            q: name,
            format: 'json',
            },
        })
        .then((response) => {
            const lon = response.data[0].lon
            const lat = response.data[0].lat
            console.log('success!', lat);
            return {lat:lat,lon:lon}
        })
        .catch((error) => {
            console.log('error!', error.response.data);
        });
        }



        const onFormSubmit = (event) =>{
            event.preventDefault();
            const coord = getCoordinates(formFields.name)
            // console.log(coord.lat)
            // console.log(coord.lon)

            const newLocation = {
                name:formFields.name,
                lat: coord['lat'],
                lon: coord['lon']
                }

            props.onUpdateLocation(newLocation)
            }

        const onLocationChange = (event) => {
            setFormFields({
                ...formFields,
                name: event.target.value
                })
            };


    return (
        <form onSubmit={onFormSubmit}>
            <div>
                <label htmlFor="city">City: </label>
                <input 
                name="city" 
                value={formFields.name}
                onChange={onLocationChange}/>
            </div>
            <input type='submit' value='Add Location'></input>
        </form>

    )
}

NewLocationForm.propTypes = {
    onUpdateLocation:PropTypes.func.isRequired,
}
export default NewLocationForm;
