import './App.css';
import { useState } from 'react'
import LocationList from './components/LocationList';
import NewLocationForm from './components/NewLocationForm';

function App() {

  // Setting state for location list
  const [locationData, setLocations] = useState([
      {
        id: 1,
        name: 'Seattle',
        lat: '47.6038321',
        lon: '-122.3300624'
      }
    ])

  const updateLocationData = (updateLocation) => {
    const newLocationData = [...locationData]
    const nextId = Math.max(...newLocationData.map(location => location.id)) + 1;
    newLocationData.push({
      id: nextId,
      name:updateLocation.name,
      lat:updateLocation.lat,
      lon:updateLocation.lon
    })
    setLocations(newLocationData)
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Get Latitude and Longitude</h1>
        <iframe src="https://giphy.com/embed/9JgeSP0jlRAVBOG9FD" width="480" height="307" frameBorder="0" className="giphy-embed" allowFullScreen=""></iframe>
        <NewLocationForm onUpdateLocation={updateLocationData}/>
        <h2>Results: </h2>
        <LocationList 
        locations={locationData}/>
      </header>
    </div>
  );
}


export default App;
