import React from "react";
import PropTypes from 'prop-types';
import { useState } from "react";
import axios from "axios";

const NewLocationForm = (props) => {

    const [formFields, setFormFields] = useState({
            name:'',
            })

    const getCoordinates = (name) => {
        let lat, lon
        axios.get('https://us1.locationiq.com/v1/search.php', {
            params: {
            key: 'pk.a4ee20e6824496da2fb04ab94b9da2dc',  // discussed below
            q: name,
            format: 'json',
            },
        })
        .then((response) => {
            lon = response.data[0].lon
            lat = response.data[0].lat
            console.log('success!', lat, lon);
            // return {lat, lon};
        }).then( nextStep => {
            console.log(lat)
            console.log(lon)
            const newLocation = {
                name:formFields.name,
                lat: lat,
                lon: lon
                }
            props.onUpdateLocation(newLocation)
        }

        )
        .catch((error) => {
            console.log('error!', error.response.data);

        });
        return {lat, lon}
        }

        // let getLocationInfo = async (name) => {
        //     // const coord = await getCoordinates(name)
        //     // let lat = coord.lat;
        //     // let lon = coord.lon;
        //     // console.log(coord.lat)
        //     // console.log(coord.lon)
        //     // return {lat, lon}
        //     return await Promise.resolve(getCoordinates(name))
        // }

        const onFormSubmit = (event) =>{
            event.preventDefault();
            getCoordinates(formFields.name)
            // console.log('here?')
            // const coord = getLocationInfo(formFields.name)

            // console.log(coord.lat)
            // console.log(coord.lon)
            // let lat = coord.lat;
            // let lon = coord.lon;

            // const newLocation = {
            //     name:formFields.name,
            //     lat: lat,
            //     lon: lon
            //     }

            // props.onUpdateLocation(newLocation)
            }

        const onLocationChange = (event) => {
            setFormFields({
                ...formFields,
                name: event.target.value
                })
            };


    return (
        <form onSubmit={onFormSubmit}>
            <div>
                <label htmlFor="city">City: </label>
                <input 
                name="city" 
                value={formFields.name}
                onChange={onLocationChange}/>
            </div>
            <input type='submit' value='Add Location'></input>
        </form>

    )
}

NewLocationForm.propTypes = {
    onUpdateLocation:PropTypes.func.isRequired,
}
export default NewLocationForm;
